function updateBuckValue(e){var n=document.getElementById("buck_value");switch(e.value){case"A+ Service":n.value=20,hide_reason_field();break;case"Attendance":n.value=3,hide_reason_field();break;case"Community Involvement":n.value=3,hide_reason_field();break;case"Customer Service":n.value=5,display_reason_field();break;case"Shift Coverage":n.value=2,hide_reason_field();break;case"Other":n.value=0,display_reason_field();break;default:n.value=0,hide_reason_field()}}function display_reason_field(){var e=document.getElementById("reason_long_holder");e.style.display="table-cell"}function hide_reason_field(){var e=document.getElementById("reason_long_holder");e.style.display="none"}function toggle_reasons(){var e=document.getElementById("reason_long_options"),n=document.getElementById("reason_long"),a=document.getElementById("toggle_reasons");"block"==e.style.display?(n.style.display="block",e.style.display="none",a.innerHTML="+ Common Reasons"):(n.style.display="none",e.style.display="block",a.innerHTML="- Common Reasons"),updateLength()}function updateReasonValue(e){var n=document.getElementById("reason_long"),a=e.innerHTML;n.value=a,toggle_reasons()}function textLength(e){var n=255;return!(e.length>n)}function updateLength(){var e=document.getElementById("text_length"),n=document.getElementById("reason_long");e.innerHTML=n.value.length+"/250 characters max"}